import React from 'react'

export default function CurrencyRow(props) {
    const {
        currencyOptions,
        selectedCurrency

    } = props
  return (
    <div>
        <input type="text" className='input'/>
        <select value={selectCurrency}>
            {currencyOptions.map(option => (
                <option key={option} value= {option}>{option}</option>
            ))}
        </select> 

    </div>
  ) 
}
