import React,{useEffect, useState} from "react";
import "./App.css"
import CurrencyRow from "./CurrencyRow";
const BASE_URL = "https://v6.exchangerate-api.com/v6/5bd9fec2b7c0daf9e4e017fe/latest/USD"

function App() {
  const [currencyOptions, setCurrencyOptions] = useState([])
  const [fromCurrency, setFromCurrency] = useState()
  const [toCurrency, setToCurrency] = useState()
  const [amount, setAmount] = useState(1)

  console.log(currencyOptions)
  useEffect(() => {
    fetch(BASE_URL)
    .then(res   => res.json())
    .then(data => { 
      const firstCurrency = Object.keys(data.conversion_rates)[43]
      setCurrencyOptions([data.base_code, ...Object.keys(data.conversion_rates)])
      setFromCurrency(data.base_code)
      setToCurrency(firstCurrency)
    })
  },[])


  return ( 

  <>
  <h1>Convert</h1> 
  <CurrencyRow
  currencyOptions={currencyOptions}
  selectedCurrency={fromCurrency}  
   onChangeCurrency = {e => setFromCurrency(e.target.value )}

  />    
  <div className="equals">=</div>
  <CurrencyRow 
   currencyOptions={currencyOptions}
   selectedCurrency={toCurrency}
   onChangeCurrency = {e => setToCurrency(e.target.value )}

   /> 
  </>
  );
} 

export default App;
