import React,{useEffect, useState} from "react";
import "./App.css"
import CurrencyRow from "./CurrencyRow";
const BASE_URL = "https://v6.exchangerate-api.com/v6/5bd9fec2b7c0daf9e4e017fe/latest/USD"

function App() {
  const [currencyOptions, setCurrencyOptions] = useState([])

  useEffect(() => {
    fetch(BASE_URL)
    .then(res   => res.json())
    .then(data => {
       setCurrencyOptions(data.base_code,data.currency_rates )
    })
  },[])


  return ( 

  <>
  <h1>Convert</h1>
  <CurrencyRow />  
  <div className="equals">=</div>
  <CurrencyRow />
  </>
  );
} 

export default App;
